mapper.update:
 	update statistics_day set
		total_time_num = #{totalTimeNum}
 	where timestamp = #{timestamp}

mapper.select:
select start_time as startTime,total_time_num as totalTimeNum,timestamp
from statistics_day 
where 1=1 timestamp = #{timestamp}

select substr(start_time,6,14) as startTime,sum(total_time_num) as totalTimeNum,sum(success) as success,sum(fail) as fail  
from statistics_day 
where now_time >= #{dayTradingStartDate} and now_time <= #{dayTradingEndDate}

	and tx_code = #{txCode}


	and chnl_code = #{chnlCode}

group by substr(start_time,6,14) order by start_time

select substr(start_time,6,8) as startTime,sum(total_time_num) as totalTimeNum,sum(success) as success,sum(fail) as fail 
from statistics_day 
where now_time >= #{dayTradingStartDate} and now_time <= #{dayTradingEndDate}

	and tx_code = #{txCode}


	and chnl_code = #{chnlCode}

group by substr(start_time,6,8) order by start_time

select now_time as nowTime,sum(total_time_num) as totalTimeNum,sum(success) as success,sum(fail) as fail 
from statistics_day 
where now_time >= #{dayTradingStartDate} and now_time <= #{dayTradingEndDate}

	and tx_code = #{txCode}


	and chnl_code = #{chnlCode}

group by now_time order by start_time
