mapper.select:
select id,type,receiver,chnl_code as chnlCode,chnl_name as chnlName
from notification 
where id = #{notification.id};

    	select id,type,receiver,address,chnl_code as chnlCode,chnl_name as chnlName  from notification where 1=1 and type = #{type}

		    and receiver = #{receiver}


    	    and address = #{address}


    		and chnl_code = #{chnlCode}


    		and chnl_name = #{chnlName}

		ORDER BY id asc

    	select id,type,receiver,address,chnl_code as chnlCode,chnl_name as chnlName  from notification where 1=1

		    and type = #{type}


		    and receiver = #{receiver}


    	    and address = #{address}


    		and chnl_code = #{chnlCode}


    		and chnl_name = #{chnlName}

		ORDER BY id asc
