mapper.select:
select SUM(total_num) as totalNum,SUM(success) as success,SUM(fail) as fail,SUM(fail)/SUM(total_num) as failRate
from trading 
where 1=1 and now_time between #{tradingStartDate} and #{tradingEndDate}

	select SUM(a.total_num) as totalNum,SUM(a.fail) as fail
   	from trading a join tx_code_info b on a.tx_code = b.tx_code
	where 1=1 and b.tx_type=1 and now_time between #{tradingStartDate} and #{tradingEndDate}
